# ====================== 主程序 ======================
def main():
    # 转为 Path 对象
    source_dirs = [Path(p) for p in SOURCE_DIRS]
    target_dir = Path(TARGET_DIR)
    # 检查源目录是否存在
    for d in source_dirs:
        if not d.exists():
            print(f"[WARN] 源目录不存在：{d}")
    # 创建目标目录
    target_dir.mkdir(parents=True, exist_ok=True)
    total = len(CAR_IDS)
    copied = 0
    missed = 0
    print(f"共需提取 {total} 个车辆子文件夹...")
    print("-" * 60)
    for car_id in CAR_IDS:
        found = False
        for src_root in source_dirs:
            src_path = src_root / car_id
            if src_path.is_dir():
                found = True
                dst_path = target_dir / car_id
                # 如果目标已存在，选择是否覆盖：当前设置为覆盖
                if dst_path.exists():
                    print(f"[INFO] 目标已存在，删除后重新复制：{dst_path}")
                    shutil.rmtree(dst_path)
                try:
                    # Python 3.8+ 有 dirs_exist_ok=True，这里我们先确保目标不存在再 copytree
                    shutil.copytree(src_path, dst_path)
                    print(f"[OK] 已复制 {car_id} : {src_path} -> {dst_path}")
                    copied += 1
                except Exception as e:
                    print(f"[ERROR] 复制 {car_id} 时出错：{e}")
                break # 已经找到就不去其他源目录找了
        if not found:
            print(f"[MISS] 未在任何源目录中找到车辆子文件夹：{car_id}")
            missed += 1
    print("-" * 60)
    print(f"处理完成！共需 {total} 个，成功复制 {copied} 个，未找到 {missed} 个。")
if __name__ == "__main__":
    main()